bool f(int x){
    if(x&lt;=1) return false;
    if(x==2) return true;
    else{
        for(int i=2;i&lt;=sqrt(x)+1;i++){
            if(x%i==0){
                return false;
            }
        }
    }
    return true;
}
void solve(){
    int x,k;
    cin&gt;&gt;x&gt;&gt;k;
    bool ok=f(x);
    if(ok==true&amp;&amp;k==1){
        cout&lt;&lt;"YES"&lt;&lt;"\n";
        return ;
    }
    if(x==1&amp;&amp;k==2){
        cout&lt;&lt;"YES"&lt;&lt;"\n";
        return ;
    }
    cout&lt;&lt;"NO"&lt;&lt;"\n";
    //cout&lt;&lt;ok&lt;&lt;"\n";
}
