C++<pre><code>#include &lt;bits/stdc++.h&gt; 
using namespace std;
/* run this program using the console pauser or add your own getch, system("pause") or input loop */
const int maxx=2e5+5;
int n,k;
int a[maxx],b[maxx];
bool check(int mid){
    int cnt=0;
    int cur_mex=0;
    for(int i=1;i&lt;=n;i++){
        if(a[i]&lt;=n+1){
            b[a[i]]=1;
            //记录v[i]出现过没有 
        }
        while(b[cur_mex]){
            //当前的这个值作为mex的值已经出现了； 
            cur_mex++;
        }
        //当这个值到目标值的时候就停止 
        if(cur_mex&gt;=mid){
            for(int j=0;j&lt;mid+1;j++){
                b[j]=0;
            }
            cur_mex=0;
            cnt++;
        }
    }
    memset(b,0,sizeof(b));
    //多的段数可以，那么少的段数一定也可以，因为可以合并； 
    if(cnt&gt;=k) return true;
    else return false;
}
void solve(){
    cin&gt;&gt;n&gt;&gt;k;
    for(int i=1;i&lt;=n;i++){
        cin&gt;&gt;a[i];
    }
    int l=0,r=n+1;
    while(l+1&lt;r){
        int mid=(l+r)/2;
        if(check(mid)) l=mid;
        else r=mid;
    }
    cout&lt;&lt;l&lt;&lt;"\n";
}
signed main(int argc, char** argv) {
    ios :: sync_with_stdio(false);
    cin.tie(0), cout.tie(0);
    int t=1;
    cin&gt;&gt;t;
    while(t--){
        solve();
    }
    return 0;
}
</code></pre>

