using namespace std;
/* run this program using the console pauser or add your own getch, system("pause") or input loop */

void solve(){
    int n,m;
    cin&gt;&gt;n&gt;&gt;m;
    string a[n];
    for(int i=0;i&lt;n;i++){
        cin&gt;&gt;a[i];
    }
    string b[m][n];
    for(int i=0;i&lt;m;i++){
        for(int j=0;j&lt;n;j++){
            cin&gt;&gt;b[i][j];
        }
    }
    int x=0;
    bool ok[n];
    for(int i=0;i&lt;n;i++){
        ok[i]=false;
    }
    for(int i=0;i&lt;m;i++){
        int cnt=0;
        for(int j=0;j&lt;n;j++){
            if(a[j]==b[i][j]){
                cnt++;
                ok[j]=true;
            }
        }
        x=max(x,cnt);
    }
    for(int i=0;i&lt;n;i++){
        if(ok[i]==false){
            cout&lt;&lt;"-1"&lt;&lt;"\n";
            return ;
        }
    }
    cout&lt;&lt;n+2*(n-x)&lt;&lt;"\n";
}
//感觉最长的那个只需要先使用n次就可以使得那些变为我们想要的，也就是x;
//然后还剩下2(n-x)个字符串需要变;
signed main(int argc, char** argv) {
    ios :: sync_with_stdio(false);
    cin.tie(0), cout.tie(0);
    int t=1;
    cin&gt;&gt;t;
    while(t--){
        solve();
    }
    return 0;
}
