C++<pre><code>#include &lt;bits/stdc++.h&gt; 
using namespace std;
/* run this program using the console pauser or add your own getch, system("pause") or input loop */
#define ll long long
void solve(){
     ll n,k,x;
     cin&gt;&gt;n&gt;&gt;k&gt;&gt;x;
     vector&lt;ll&gt; v(n+1);
     ll sum=0;
     for(int i=1;i&lt;=n;i++){
        cin&gt;&gt;v[i];
        sum+=v[i];
     }
     if(sum*k&lt;x){
        cout&lt;&lt;0&lt;&lt;"\n";
        return ;
     }
     ll p=x%sum,q=x/sum;
     if(p==0){
        ll ans=n*k-q*n+1;
        cout&lt;&lt;ans&lt;&lt;"\n";
        return ;
     }
     ll sum1=0,j=0;
     for(int i=n;i&gt;=1;i--){
         sum1+=v[i];
         if(sum1&gt;=p){
            j=i;
            break;
         }
     }
     ll cn=n*(k-1)-q*n+j;
     cout&lt;&lt;cn&lt;&lt;"\n";
}
//3 4 2 1 5  3 4 2 1 5   3 4 2 1 5
signed main(int argc, char** argv) {
    ios :: sync_with_stdio(false);
    cin.tie(0), cout.tie(0);
    int t=1;
    cin&gt;&gt;t;
    while(t--){
        solve();
    }
    return 0;
}
</code></pre>

