using namespace std;
/* run this program using the console pauser or add your own getch, system("pause") or input loop */
#define ll long long
void solve() {
    int n;
    cin&gt;&gt;n;
    vector&lt;int&gt; a(n+1),b(n+1);
    map&lt;int,int&gt; mp1,mp2;
    vector&lt; pair&lt;int,int&gt; &gt; v;
    for(int i=1; i&lt;=n; i++) {
        cin&gt;&gt;a[i];
        mp1[a[i]]=i;
    }
    for(int i=1; i&lt;=n; i++) {
        cin&gt;&gt;b[i];
        mp2[b[i]]=i;
    }
    int cnt=0;
    for(int i=1; i&lt;=n; i++) {
        if(a[i]==b[i]) {
            cnt++;
        }
    }
    if(cnt&gt;=2){
        cout&lt;&lt;"-1"&lt;&lt;"\n";
        return ;
    }
    if(cnt==1&amp;&amp;n%2==0){
        cout&lt;&lt;"-1"&lt;&lt;"\n";
        return ;
    }
    if(cnt!=1&amp;&amp;n%2==1){
        cout&lt;&lt;"-1"&lt;&lt;"\n";
        return ;
    }
    if(cnt==1){
        for(int i=1;i&lt;=n;i++){
            if(a[i]==b[i]&amp;&amp;i!=n/2+1){
                if(i&gt;n/2+1){
                    v.push_back({n/2+1,i});
                }
                else{
                    v.push_back({i,n/2+1});
                }
                swap(mp1[a[i]],mp1[a[n/2+1]]);
                swap(mp2[b[i]],mp2[b[n/2+1]]);
                swap(a[i],a[n/2+1]);
                swap(b[i],b[n/2+1]);
                break;
            }
        }
    }
    for(int i=1; i&lt;=n/2; i++) {
        if(a[i]!=b[n-i+1]) {
            int x=mp2[a[i]];
            v.push_back({x,n-i+1});
            swap(mp1[a[x]],mp1[a[n-i+1]]);
            swap(mp2[b[x]],mp2[b[n-i+1]]);
            swap(a[x],a[n-i+1]);
            swap(b[x],b[n-i+1]);
        }
    }
    bool ok=false;
    for(int i=1;i&lt;=n;i++){
        if(a[i]!=b[n-i+1]){
            ok=true;
            break;
        }
    }
    if(ok){
        cout&lt;&lt;"-1"&lt;&lt;"\n";
        return ;
    }
    cout&lt;&lt;v.size()&lt;&lt;"\n";
    for(int i=0;i&lt;v.size();i++){
        cout&lt;&lt;v[i].first&lt;&lt;" "&lt;&lt;v[i].second&lt;&lt;"\n";
    }
}
signed main(int argc, char** argv) {
    ios :: sync_with_stdio(false);
    cin.tie(0), cout.tie(0);
    int t=1;
    cin&gt;&gt;t;
    while(t--) {
        solve();
    }
    return 0;
}
</code></pre>

