void solve(){
    ll n,k;
    cin&gt;&gt;n&gt;&gt;k;
    ll dp[n+5][k+5],a[n+5][k+5];
    memset(dp,0,sizeof(dp));
    memset(a,0,sizeof(a));
    for(int i=1;i&lt;=n;i++){
        for(int j=1;j&lt;=k;j++){
            cin&gt;&gt;a[i][j];
        }
    }
    //dp[i][j]前面i被分了j段
    for(int i=1;i&lt;=n;i++){
        for(int j=1;j&lt;=k;j++){
            dp[i][j]=max(dp[i-1][j]+a[i][j],dp[i][j]);
            dp[i][j]=max(dp[i][j],dp[i][j-1]);
        }
    }
    cout&lt;&lt;dp[n][k]&lt;&lt;'\n';
}
