void solve() {
    double p;
    int n,k;
    cin&gt;&gt;p&gt;&gt;n&gt;&gt;k;
    vector&lt;double&gt; v;//打折券 
    vector&lt;int&gt; f;
    v.push_back(1);
    f.push_back(0);
    for(int i=1;i&lt;=n;i++){
        int ti,pi;
        cin&gt;&gt;ti&gt;&gt;pi;
        if(ti==0){
            v.push_back((double)pi/10);
        }else{
            f.push_back(pi);
        }
    }
    sort(v.begin()+1,v.end());
    sort(f.begin()+1,f.end(),greater&lt;int&gt;());
    int sv=v.size()-1,sf=f.size()-1;
    for(int i=1;i&lt;=sv;i++){
        v[i]*=v[i-1];
    }
    for(int i=1;i&lt;=sf;i++){
        f[i]+=f[i-1];
        //cout&lt;&lt;f[i]&lt;&lt;" ";
    }
    double ans=p;
    for(int i=0;i&lt;=sv;i++){
        if(i&gt;=k){
            ans=min(ans,p*v[k]);
        }else{
            ans=min(ans,p*v[i]-f[k-i]);
        }
    }
    ans=max(ans,0.0);
    cout&lt;&lt;fixed&lt;&lt;setprecision(2)&lt;&lt;ans&lt;&lt;endl;
}
