C++<pre><code>#include &lt;bits/stdc++.h&gt; 
using namespace std;
/* run this program using the console pauser or add your own getch, system("pause") or input loop */
#define ll long long
const int maxx=2e5+5;
ll a[maxx],tree[maxx];
int n,q;
int lowbit(int x){
    return x&amp;(-x);
}
void updata(int x,int k){
    for(int i=x;i&lt;=n;i+=lowbit(i)){
        tree[i]+=k;
    }
}
ll query(int x){
    ll sum=0;
    for(int i=x;i&gt;=1;i-=lowbit(i)){
        sum+=tree[i];
    }
    return sum;
}
void solve(){
    memset(tree,0,sizeof(tree));
    cin&gt;&gt;n&gt;&gt;q;
    for(int i=1;i&lt;=n;i++){
        cin&gt;&gt;a[i];
        updata(i,a[i]);
    }
    ll ans=0,len=1;
    while(q--){
        int op,x,y;
        cin&gt;&gt;op&gt;&gt;x&gt;&gt;y;
        if(op==1){
            int diss=y-a[x];
            updata(x,diss);
            a[x]=y;
        }
        else if(op==2){
            ll z=query(y)/100-query(x-1)/100;
            ans=ans^(z*len);
            len++;
        }
    }
    cout&lt;&lt;ans&lt;&lt;"\n";
}
signed main(int argc, char** argv) {
    ios :: sync_with_stdio(false);
    cin.tie(0), cout.tie(0);
    int t=1;
    cin&gt;&gt;t;
    while(t--){
        solve();
    }
    return 0;
}
</code></pre>

