void solve(){
    int n,m,v;
    cin&gt;&gt;n&gt;&gt;m&gt;&gt;v;
    vector&lt;int&gt; a(n+1);
    for(int i=1;i&lt;=n;i++){
        cin&gt;&gt;a[i];
    }
    memset(dp,0,sizeof(dp));
