void dijkstra(){
    for(int i=1;i&lt;=n;i++){
        dis[i]=INF;
    }
    dis[s]=0;
    q.push({0,s});
    while(!q.empty()){
        pair&lt;int,int&gt; p=q.top();
        q.pop();
        int u=p.second;
        if(vis[u]) continue;
        vis[u]=true;
        for(int v=0;v&lt;g[u].size();v++){
            int to=g[u][v].first,w=g[u][v].second;
            if(dis[to]&gt;dis[u]+w){
                dis[to]=dis[u]+w;
                q.push({dis[to],to});
            }
        }
    }
    cout&lt;&lt;dis[e]&lt;&lt;endl;
}
void solve(){
    cin&gt;&gt;n&gt;&gt;m;
    vector&lt;int&gt; v(m+1),u(m+1),k(m+1);
    for(int i=1;i&lt;=m;i++){
        int sv,su,ki;
        cin&gt;&gt;sv&gt;&gt;su&gt;&gt;ki;
        if(sv&gt;su) swap(sv,su);
        v[i]=sv,u[i]=su,k[i]=ki;
    }
    cin&gt;&gt;d;
    memset(a,0,sizeof(a));
    for(int i=1;i&lt;=d;i++){
        int x,y;
        cin&gt;&gt;x&gt;&gt;y;
        if(x&gt;y) swap(x,y);
        a[x][y]=1;
    }
    for(int i=1;i&lt;=m;i++){
        int x=v[i],y=u[i];
        if(a[x][y]==1){
            add_edge(x,y,k[i]);
        }
        else{
            add_edge(x,y,0);
        }
    }
    cin&gt;&gt;s&gt;&gt;e;
    dijkstra();
}
signed main(int argc, char** argv) {
    ios :: sync_with_stdio(false);
    cin.tie(0), cout.tie(0);
    int t=1;
    //cin&gt;&gt;t;
    while(t--){
        solve();
    }
    return 0;
}
